import React, {Component} from 'react';

class Seances extends Component{
    constructor(props){
        super(props);
        this.state = {
          item:[],
          isLoaded: false,
        }
    }
    componentDidMount() {
        fetch('https://back.staging.bsport.io/api/v1/offer/?min_date=2020-04-13&max_date=2020-04-19&company=6&page_size=30')
        .then(res => res.json()
        .then(json=> {
          this.setState({
            isLoaded:true,
            items:json,
          })
        }))
      }

  render(){
    var{isLoaded, items} = this.state;
    if (!isLoaded){
        return <div>Loading...</div>
    } 
    else { 
    return (
     <div>
          {items.map((item)=>(
             <li>
                Début de la séance : {item.links.next}
                <br/>
                Durée : {item.results.duration_minute}
                <br/>
                Effectif : {item.results.effectif}

             </li>
         ))}

          </div>
    );
  }
}
  }
  export default Seances;
