import React, {Component} from 'react';

class Seances extends Component{
    state = {
        items:[],
        }
    componentDidMount() {
        fetch('https://back.staging.bsport.io/api/v1/offer/?min_date=2020-04-13&max_date=2020-04-19&company=6&page_size=30')
        .then(res => res.json()
        .then(json=> {
          this.setState({
            items:json,
          })
        }))
      }

  render(){
      var items = this.state;
    return (
     <div>
         {items.map((item)=>(
             <li>
                Début de la séance : {item.results.date_start}
                <br/>
                Durée : {item.results.duration_minute}
                <br/>
                Effectif : {item.results.effectif}
             </li>
         ))}

          </div>
    );
  }
  }
  export default Seances;
