        if (!isLoaded){
            return <div>Loading...</div>
        } 
        else { 
        return(
            <div>
                {items.map(item=>(
                        <Card id="filmCard" style={{maxWidth: 345,}} >
                          <CardActionArea>
                            <CardContent>
                              <Typography gutterBottom variant="h5" component="h2">
                                  {item.results}
                              </Typography>
                            </CardContent>
                          </CardActionArea>
                        </Card>
                    ))
                }
            </div>
        )
        }
        }
}


