import React, {Component} from 'react';

class Seances extends Component {
    constructor(props){
        super(props);
        this.state = {
          item: {},
          isLoaded: false,
        }
      }
      componentDidMount() {
        fetch('https://back.staging.bsport.io/api/v1/offer/?min_date=2020-04-13&max_date=2020-04-19&company=6&page_size=30')
        .then(res => res.json()
        .then(json=> {
          this.setState({
            isLoaded:true,
            item:json,
          })
        }))
      }


    render(){
        var{isLoaded, item} = this.state;
        if (!isLoaded){
            return <div>Loading...</div>
        } 
        else { 
      return (
          <div>
              {item.results}

          </div>

        );
    }
}
  }


export default Seances;
